import React from 'react';
// import logo from './logo.svg';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  return (
    <div className="App">
      <div className="formwrap">
        <div className="col-lg-6 col-lg-offset-3 col-md-6 col-md-offset-3 col-sm-12 col-xs-12 formwrap1">
           <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
              <div className="form-group" >
                <label htmlFor="FirstName" className="fname">First Name <span className="asterik">*</span></label>
                  <div className="input-group" id="erroralert">
                    <span className="input-group-addon"> <i className="fa fa-user"> </i> </span>
                       <input  type="text" className="form-control"placeholder="First Name" name="FirstName" 
                           defaultValue = {this.state.FirstName}
                              onChange    = {this.handleChange.bind(this)}
                         ></input>
                    </div>
                </div>
           </div>
        </div>


      </div>
    </div>
  );
}

export default App;
