import React,{Component}from 'react';
import './Form.css';
import '@fortawesome/fontawesome-free/css/all.min.css';
import Axios from 'axios';
import Swal from 'sweetalert2';
import $ 		from 'jquery';
import jQuery 	from 'jquery';
import validate from "jquery-validation";


export default class Form extends Component{
    constructor(props){
        super(props)
        this.state = {
            Submit  :true,
            Name    :"",
            message :"",
            mail    :""
        }
    }
    componentDidMount(){

        $.validator.addMethod("regexifsc", function (value, element, regexpr) {
            return regexpr.test(value);
          }, "Special Characters Not Allowed.");


          $.validator.addMethod("regexifsc1", function (value, element, regexpr) {
            return regexpr.test(value);
          }, "Enter valid e-mail");



          $("#contactmodalId").validate({
          rules: {
            Name: {
              required: true,
              regexifsc:/^[A-Za-z]+$/
            },
            message: {
                required: true,
                regexifsc:/^[A-Za-z]+$/
              },
            mail: {
              required: true,
              regexifsc1:/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+))|("[\w-\s]+")([\w-]+(?:\.[\w-]+)))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i,
            },
            errorPlacement : function(error, element)
            {
                if(element.attr("Name") === "FirstName"){
                   error.insertAfter("#erroralert")
               }
               if(element.attr("Name") ==="MiddleName"){
                   error.insertAfter("#erroralert1")
               }
               if(element.attr("Name") === "LastName"){
                   error.insertAfter("#erroralert2")
               }
            } 

            }
          });
    }


    handleChange(event){
        var name = event.currentTarget.name;
        this.setState({[name] : event.currentTarget.value});
      }
      handleSubmit(event){
          event.preventDefault();
          console.log(event)
          var submit =true;

          var formValues = {

                            "Name"     :this.state.Name,
                            "mail"     :this.state.mail,
                            "message"  :this.state.message,  
                         }
                         console.log("formValues = ",formValues);

      }

    render(){
        return(
            <div>
                <div className="formwrap">
                     <div className="col-lg-6 col-lg-offset-3 col-md-6 col-md-offset-3 col-sm-12 col-xs-12 formwrap1" id="contactmodalId">
                         <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                             <div className="form-group" >
                                <label htmlFor="FirstName" className="fname">Name <span className="asterik">*</span></label>
                                    <div className="input-group" id="erroralert">
                                         <span className="input-group-addon"> <i className="fa fa-user"> </i> </span>
                                            <input  type="text" className="form-control"placeholder="" name="Name" 
                                                 defaultValue = {this.state.Name}
                                                   onChange    = {this.handleChange.bind(this)}
                                             ></input>
                                        </div>
                                    </div>
                                </div>
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                     <div className="form-group" >
                                         <label htmlFor="FirstName" className="email">Email Address <span className="asterik">*</span></label>
                                            <div className="input-group" id="erroralert">
                                                 <span className="input-group-addon"> <i className="fa fa-user"> </i> </span>
                                                     <input  type="text" className="form-control"placeholder="mail" name="mail" 
                                                  defaultValue = {this.state.mail}
                                                  onChange    = {this.handleChange.bind(this)}
                                             ></input>
                                        </div>
                                    </div>
                                </div>
                                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12">
                                     <div className="form-group" >
                                         <label htmlFor="FirstName" className="message">Message <span className="asterik">*</span></label>
                                            <div className="input-group" id="erroralert">
                                                 <span className="input-group-addon"> <i className="fa fa-user"> </i> </span>
                                                     <textarea  type="text" className="form-control"placeholder="" name="message" row="2" column="10" 
                                                     defaultValue = {this.state.message}
                                                     onChange    = {this.handleChange.bind(this)}
                                             ></textarea>
                                        </div>
                                    </div>
                                </div>
                                <button className="btn btn-primary col-lg-3 subButton pull-right" onClick={this.handleSubmit.bind(this)}>Submit</button>

                            </div>
                        </div>
                    </div>
        )

    }


}
