import React,{Component}from 'react';
// import './ContactUsForm.css';
import axios from 'axios';
// import swal from 'sweetalert';
// import PlacesAutocomplete from 'react-places-autocomplete';
// import {geocodeByAddress,geocodeByPlaceId,getLatLng,} from 'react-places-autocomplete';
import $ 		from 'jquery';
 import jQuery 	from 'jquery';
 import validate from "jquery-validation";


export default class Form extends Component{

    constructor(props) {
        super(props);
        this.state = {

            submit:true,
            "fullname"   :"",
            "address "           :"",
            "phone"              :"",
            "email"               :"",
            "message"            :"",
            "buttonText"        : "Submit",


        };
    }

    onhandleChange = address => {
        this.setState({ address });
      };


    handleChange(event) {
        event.preventDefault();
        const name = event.target.name;
        this.setState({
            [name]: event.target.value
        });
    }
    componentDidMount(){
        $.validator.addMethod("reglx",function(value,element,regexpr){
            return regexpr.test(value);
        }, "This field is required");
        $.validator.addMethod("reglx1", function (value, element, regexpr) {
            return regexpr.test(value);
          }, "Enter valid e-mail");

        $("#validateApplication").validate({
            rules: {
                fullname:{
                required:true,
                reglx:/^[A-Za-z]+$/

                },

                address:{
                    required:true,
                    reglx:/^[A-Za-z]+$/

                    },

                phone:{
                        required:true,
                            reglx:/^[0-9]+$/
                     },
                email:{
                        required:true,
                         reglx1:/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$|^$)/,
                         },

                message:{
                            required:true,
                            reglx:/^[A-Za-z]+$/

                            }
            },
            errorPlacement : function(error, element)
             {
			 	if(element.attr("Name") === "fullname"){
					error.insertAfter("#erroralert")
                }
                if(element.attr("Name") ==="phone"){
					error.insertAfter("#erroralert1")
                }
                if(element.attr("Name") ==="email"){
					error.insertAfter("#erroralert2")
                }
               if(element.attr("Name") ==="address"){
					error.insertAfter("#erroralert4")
                }
                if(element.attr("Name") ==="message"){
					error.insertAfter("#erroralert3")
                }
            },






        })
    }

    submitPatientEnquiry(event){
        event.preventDefault();
        var patientEnquiryData = {

            fullname                  : this.state.fullname,
            phone                     : this.state.phone,
            email                    : this.state.email,
            address                 : this.state.address,
            message                 : this.state.message,

        }
        if($("#validateApplication").valid()){
        axios.post('/api/patientmaster/send/enquiry', patientEnquiryData)
        .then((response) => {
        //   swal("Thank you for getting in touch!", "We appreciate you contacting to trüU Smiles ", "We will get in touch with you shortly.");

            this.setState({

              "fullname"          : "",
              "phone"             : "",
              "email"             : "",
              "address"         : "",
              "message"         :""

          },()=>{

          })

      })
      .catch((error) => {
          console.log("error while saving patients Details",error)
      });

            this.setState({

                "fullname"          : "",
                "phone"             : "",
                "email"             : "",
                "address"           : "",
                "message"           :""

            });

            // swal("Thank you for getting in touch!"," We appreciate you contacting to trüU Smiles. We will get in touch with you shortly.");
      }
    }

    render(){
        return(
            <div className="contacformwrap">
                <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12 contactformwrap2">
                   <div className="col-lg-6 col-md-6 col-sm-12 col-xs-12 leftsideIMgwrap"> 
                        <img src="/images/Bus.jpg" alt="contactus" className="leftimage img-responsive" />  
                    </div>   
                        <div className="col-lg-6  col-md-6 col-sm-12 col-xs-12 formwrap">
                            <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12"> 
                                <div className="hetitleform"><b>CONTACT </b> trüU</div>
                           </div> 
                           <form className="col-lg-12 col-md-12 col-sm-12 col-xs-12" id="twoFieldFormValid" id="validateApplication">
                               <div className="row">
                               <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12 formwraphight2 customfieldht1">
                               <div className="form-margin col-lg-6  col-md-6 col-sm-6 col-xs-12 pdcls inputBoxMargin cusboxmargin">
                                     <div id="textFieldOne" className="stylishinput contactUsInputMmargintop" id="erroralert">
                                        <input className="inputBoxAnimation" type="text" id="appointmentDate" required placeholder="" name="fullname" ref="fullname" 
                                             value={this.state.fullname} onChange={this.handleChange.bind(this)} />
                                                <label htmlFor="name" className="labelCustomeStyle customfontweight">First and Last Name<i className="astrick">*</i></label>
                                     </div>
                                </div>
                                 <div className="form-margin col-lg-6  col-md-6 col-sm-6 col-xs-12 pdcls inputBoxMargin simpleInputBox cusboxmargin mobileview ">
                                    <div id="textFieldOne" className="stylishinput contactUsInputMmargintop" id="erroralert4">
                                         <input className="inputBoxAnimation" type="text" id="duration" required placeholder=""  name="address" ref="address"  
                                             value={this.state.address} onChange={this.handleChange.bind(this)}/> 
                                        <label htmlFor="name" className="labelCustomeStyle simpleLabelStyle customfontweight">Your Location<i className="astrick">*</i></label> 
                                    </div>
                                </div>
                            </div>
                        </div>
                         <div className="row">
                            <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12 customfieldht2">
                                <div className="form-margin col-lg-6  col-md-6 col-sm-6 col-xs-12 pdcls inputBoxMargin simpleInputBox cusboxmargin">
                                    <div id="textFieldOne" className="stylishinput contactUsInputMmargintop" id="erroralert1">
                                        <input className="inputBoxAnimation" type="text" id=""required placeholder=""  name="phone" ref="phone" 
                                             value={this.state.phone} onChange={this.handleChange.bind(this)}/>
                                                 <label htmlFor="name" className="labelCustomeStyle simpleLabelStyle customfontweight">Phone<i className="astrick">*</i></label>
                                    </div>
                                </div>
                                <div className="form-margin col-lg-6  col-md-6 col-sm-6 col-xs-12 pdcls inputBoxMargin simpleInputBox cusboxmargin">
                                    <div id="textFieldOne" className="stylishinput contactUsInputMmargintop" id="erroralert2">
                                        <input className="inputBoxAnimation" type="text"required placeholder=""  name="email" ref="email" 
                                            value={this.state.email} onChange={this.handleChange.bind(this)}/>
                                                <label htmlFor="name" className="labelCustomeStyle simpleLabelStyle customfontweight">E-Mail<i className="astrick">*</i></label>
                                    </div>
                                </div> 
                             </div>
                            </div>
                                <div className="row">
                                    <div className="col-lg-12 col-md-12 col-sm-12 col-xs-12 customfieldht3">
                                         <div className="form-margin col-lg-12  col-md-12 col-sm-12 col-xs-12 pdcls inputBoxMargin simpleInputBox cusboxmargin">
